/*texto de proxy y cache*/

a.text-info {
visibility: hidden;

}

a.text-info::after {
visibility: visible;
}

/*Icono de proxy*/
span.glyphicon.glyphicon-sort {
 visibility: hidden;
 top: 5px;
 left: -50px;
 filter: invert(30%) sepia(74%) saturate(530%) hue-rotate(141deg) brightness(92%) contrast(88%);
 }

span.glyphicon.glyphicon-sort:after {
content: url(https://icons.getbootstrap.com/assets/icons/shield-lock.svg);
visibility: visible;

}
/*Icono de Cache*/
span.glyphicon.glyphicon-link {
 visibility: hidden;
 top: 5px;
 left: -10px;
 filter: invert(30%) sepia(74%) saturate(530%) hue-rotate(141deg) brightness(92%) contrast(88%);



}

span.glyphicon.glyphicon-link::after {
 content: url(https://icons.getbootstrap.com/assets/icons/archive.svg);
 visibility: visible;
}
/*Boton para ver mas*/
label.infobox_toggle.panel-footer {
 background-color: #F8F9FB;

 margin-top: 5px;

}
/*objeto detras de tag engines*/
div.pull-right {
 position: absolute;
transform:translate(5px, 10px);
max-height: 5px;

}
/*boton de searx*/
span.instance.pull-left {
 visibility: hidden;

}
/*Searx home*/
span.instance.pull-left:after {
Visibility: hidden;


}


